INSERT INTO `noticias` (not_id, not_titulo, not_fonte, not_descricao, not_quando) VALUES (0, 'Estudo do Greenpeace aponta que grandes empresas européias rejeitam alimentos transgênicos', '', 'Um estudo elaborado pelo Greenpeace Internacional mostra que 90% dos maiores varejistas e 73% dos grandes fabricantes de alimentos e bebidas da União Européia adotaram uma política de não utilização de transgênicos em seus negócios no mercado europeu para atender à nova legislação européia de rotulagem.', '20051006022127');
